// Helper function to get translation
export const getTranslation = (key: keyof typeof type1Translations, locale?: string): string => {
  const currentLocale = locale || (typeof window !== 'undefined' ? localStorage.getItem('custom_locale') || 'ru' : 'ru')
  const translation = type1Translations[key]
  if (translation) {
    return translation[currentLocale as 'uz' | 'ru' | 'uzcryl'] || translation.ru || key
  }
  return key
};

// Helper function to get type name translation
export const getTypeNameTranslation = (typeKey: 'type1' | 'type2' | 'type3' | 'type4', locale?: string): string => {
  const currentLocale = locale || (typeof window !== 'undefined' ? localStorage.getItem('custom_locale') || 'ru' : 'ru')
  const translation = typeNameTranslations[typeKey]
  if (translation) {
    return translation[currentLocale as 'uz' | 'ru' | 'uzcryl'] || translation.ru || typeKey
  }
  return typeKey
};
